import Chrome from "chrome";

declare namespace chrome {
  export default Chrome;
}

declare module "*.svg" {
  const src: string;
  export default src;
}

declare module "*.jpg" {
  const content: string;
  export default content;
}

declare module "*.png" {
  const content: string;
  export default content;
}

declare module "*.json" {
  const content: string;
  export default content;
}
